CREATE DATABASE student;
use student;
CREATE TABLE student(id int primary key,name varchar(20),dob varchar(20),gender varchar(10),email varchar(30),phone bigint,created_at timestamp,created_by varchar(20),modified_at timestamp,modified_by varchar(20));
SELECT * FROM student;

CREATE TABLE student_result(result_id int primary key,student_id int,branch varchar(20),CGPA int,credits int,backlogs int,created_at timestamp,created_by varchar(20),modified_at timestamp,modified_by varchar(20),foreign key(student_id) references student(id));
SELECT * FROM student_result;
UPDATE student_result SET credits = CASE WHEN CGPA>=7.5 THEN 9 ELSE 5 END;
SELECT * FROM student ORDER BY id LIMIT 5 OFFSET 5;
SELECT branch, AVG(CGPA) AS average_cgpa FROM student_result GROUP BY branch HAVING AVG(CGPA)>=5.5;
SELECT * FROM student ORDER BY gender;



create table university(id int,uni_name varchar(30),uni_id int primary key,uni_type varchar(20),uni_chancellor varchar(30),total_students int,created_at timestamp,created_by varchar(20), modified_at timestamp, modified_by varchar(20));

CREATE TABLE college_info (college_id INT PRIMARY KEY,college_name VARCHAR(90) NOT NULL,uni_id INT,college_address VARCHAR(50),no_students INT,created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,created_by VARCHAR(20),modified_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,modified_by VARCHAR(20),FOREIGN KEY (uni_id) REFERENCES university(uni_id));

UPDATE university SET uni_type=CASE WHEN total_students>=100000 THEN "public state" else "deemed" end;
SELECT * FROM college_info ORDER BY id LIMIT 15 OFFSET 15;
SELECT college_name,avg(no_students) as average_students from college_info group by college_name having avg(no_students)>=2000;
SELECT * FROM college_info ORDER BY uni_id;



CREATE TABLE manager(manager_id int primary key,name varchar(20),email varchar(20),gender varchar(20),phone bigint,department varchar(20),
created_at timestamp,created_by varchar(20),modified_at timestamp,modified_by varchar(20));
SELECT * FROM manager;

CREATE TABLE employee(employee_id int primary key,manager_id int,name varchar(20),email varchar(20),phone bigint,salary bigint, created_at timestamp,created_by varchar(20),modified_at timestamp,modified_by varchar(20),foreign key(manager_id) references manager(manager_id));
UPDATE employee SET name = CASE WHEN salary>=35000 THEN 'Harsha' ELSE 'Abhi' END;
SELECT * FROM manager ORDER BY manager_id LIMIT 5 OFFSET 5;
SELECT name, AVG(salary) AS average_salary FROM employee GROUP BY name HAVING AVG(salary)>=35000;
SELECT * FROM manager ORDER BY department